member.summary:
            This is where all buff related assignments go. For example:


Main.buffName[Type] = "Display Name";

Main.buffTip[Type] = "Buff Tooltip";

Main.debuff[Type] = true;

Main.buffNoTimeDisplay[Type] = true;

Main.vanityPet[Type] = true;

Main.lightPet[Type] = true;

example.code:
var clone = (ExampleHookItem)base.Clone();
            clone.targets = (int[])this.targets.Clone(); // Or whatever deep copy operations are relevant.
            return clone;

member.example:
if (++frameCounter > 8)
            {
            	frameCounter = 0;
            	if (++frame > 5)
            	{
            		frame = 0;
            	}
            }
            	or, to mimic another tile, simply:
            	frame = Main.tileFrame[TileID.FireflyinaBottle];
